  export default class ClockInterface extends Component {
    constructor(props) {
      super(props)
      this.state = {
        count: 60 * 25,
        myInterval: undefined
      }
      this.handleStartClick = this.handleStartClick.bind(this);
    //   this.handlePauseClick = this.handlePauseClick.bind(this);
    //   this.handleResetClick = this.handleResetClick.bind(this);
    //   this.handleStopAndResetClick = this.handleStopAndResetClick.bind(this);
    };
  
    getSeconds() {
      return ('0' + this.state.count % 60).slice(-2)
    };
    getMinutes() {
      return (Math.floor(this.state.count / 60) )
    };
  
    handleStartClick() {
      var _this = this
      if (this.state.myInterval) {
        return
      }
      const myInterval = setInterval(function() {
        _this.setState(state => ({
          count: state.count - 1
        }))
      }, 1000)
      this.setState({myInterval})
    };
    handlePauseClick() {
      clearInterval(this.state.myInterval)
      this.setState({myInterval: undefined})
    };
    handleResetClick() {
      this.setState({
        count: 60 * 25
      })
    };
    handleStopAndResetClick() {
      clearInterval(this.state.myInterval)
      this.setState({
        count: 60 * 25,
        myInterval: undefined
      })
    }
  
    render() {
      return (
        <div id='container'>
          <h1 id='clockdigits'>{this.getMinutes()}:{this.getSeconds()}</h1>
          <button id='startbutton' disabled={!!this.state.myInterval} onClick={this.handleStartClick}><i className="fa fa-play"></i>Play</button>
          <button id='pausebutton' disabled={!this.state.myInterval} onClick={this.handlePauseClick}><i className="fa fa-pause"></i>Pause</button>
          <button id='resetbutton' onClick={this.handleResetClick}><i className='fas fa-sync-alt'></i>Reset</button>
          <button id='resetbutton' disabled={!this.state.myInterval} onClick={this.handleStopAndResetClick}><i className='fas fa-sync-alt'></i>Stop &amp; Reset</button>
        </div>
      );
    }
  }
