import React, { Component } from 'react';
import jQuery from 'jquery'
import {
  shuffledGrid,
  // copyBoard,
  // isTileEqual,
  // isAdjacent,
  calculateScore
} from '../../util/gameUtil';
import Board from '../Board';
import ScoreBox from '../ScoreBox';
// import CurrentWord from '../CurrentWord';
import Button from '../Button';
import './Game.css';

//Again like in App.js since {component} was imported then in the below line it is written as extends Component
//otherwise it should be written as extends React.Component
export default class Game extends Component {
  constructor(props) {
    super(props);
    // TODO: Init board with random tiles
    this.initGrid = shuffledGrid();
    this.removableBoardArray = JSON.parse(JSON.stringify(this.initGrid));
    // TODO: Init state with the board
    this.state = {
      board: this.initGrid,
      currentWord: '',
      currentWordPosition: [],
      wordScoreList: {},
      currentLetter:'',
      removableBoardArray: this.removableBoardArray,
      removedRowsAndColumns: []
    };
  }






  // Adds Current Word to the Word List
  handleSubmit(word) 
  {

    jQuery.ajax({
      type: 'POST',
      url: 'http://localhost:3001/check_word_validity/pstinputword',
      data:{wordEntered:word},
      crossDomain: true,
      headers: {
        'Content-Type': 'application/x-www-form-urlencoded',
        'Access-Control-Allow-Origin': '*'
      }

    }).done(data => {
      console.log(data);
    });
    // TODO: Check if Current Word is valid

    // Check if word is valid
    if (word.length < 3 || this.state.wordScoreList[word]) {
      return;
    }
    // TODO: Calculate score
    const score = calculateScore(word);

    // TODO: Unselect all tiles.
    const clearedBoard = this.initGrid;

    // const clearedRemovableBoardArray = JSON.parse(JSON.stringify(this.initGrid));

    // TODO: Add to the Word List
    this.setState({
      // wordScoreList: Object.assign(this.state.wordScoreList, {[word]: score}),
      wordScoreList: { ...this.state.wordScoreList, [word]: score },
      currentWord: '',
      currentWordPosition: [],
      board: clearedBoard
      // ,removableBoardArray:clearedRemovableBoardArray
    });

  }

  handleChange = (e) =>
  {
    // this.setState({currentWord:e.target.value});
  }



  handleKeyPress = (e) =>
  {
    if ((e.keyCode >= 65 && e.keyCode <= 90) || (e.keyCode >= 97 && e.keyCode <= 122))
    {

      let returnedRowAndColumn = testForLettersInBoard(e,this.state.removableBoardArray) ;


      if ( returnedRowAndColumn.row !== -1 && returnedRowAndColumn.column !== -1 )
        { 
            const newItems = this.state.removableBoardArray;
            newItems[returnedRowAndColumn.row][returnedRowAndColumn.column] = "";

            this.setState({removableBoardArray:newItems});
            this.setState({currentLetter:e.key});
            this.setState({currentWord:this.state.currentWord + e.key});

        }
      else
      {
        alert("The letter either doesn't exist in board or you have already chosen maximum number of that letter in board");
      }
      }
    else if (e.keyCode === 8)
    {
      this.setState({currentWord:this.state.currentWord.substring(0, this.state.currentWord.length - 1)});
    }
    else
    {
      alert("only alphabetic characters and backspace is allowed for deletion.");
    }
  }


  render() {
    return (
      <div>
        <div className="game-area">
          <Board
            board={this.state.board}
          />

          {/* <CurrentWord currentWord={this.state.currentWord}  label="Current Word"> */} 
          {/* {/* </CurrentWord> */}

          <div className="word-area">
            <div className="current-header">
              Current Word
            </div>
           <div className="current-word">

            <input type="text" 
            onKeyDown = {this.handleKeyPress} value= {this.state.currentWord} onChange={this.handleChange}
            ></input>
            </div>
          </div>

          <Button
            // TODO: Pass Button Props and Button Callback
            handleSubmit={this.handleSubmit.bind(this, this.state.currentWord)}
            label="SUBMIT WORD"
          />
        </div>

        <ScoreBox
          // TODO: Pass ScoreBox Props
          wordScoreList={this.state.wordScoreList}
          totalScore={Object.values(
            this.state.wordScoreList
          ).reduce((totalScore, next) => {
            return totalScore + next;
          }, 0)}
        />
        {/* Makes Board and ScoreBox be side by side */}
        <div className="clear" />

      </div>
    );
  }
}

function testForLettersInBoard(e,boardState)
{

  for(let i=0;i<boardState.length;i++)
  {
   for(let j=0; j<boardState[i].length;j++)
   {
     if(e.key === boardState[i][j].letter)
     {
        return {row:i,column:j};
     }
   } 
  }
  return {row:-1,column:-1};
}
