import React,{Component,useRef} from 'react';  
import Modal from "react-bootstrap/Modal";
import Button from "react-bootstrap/Button";
import '../../bootstrap.min.css';
import Game from '../Game';
import ScoreBox from '../ScoreBox'
import WordScoreList from '../ScoreBox/WordScoreList';
import TotalScore from '../ScoreBox/TotalScore';
export default class Result extends Component{
  
  constructor(props) 
  {
    super();
    this.state = { 
      show: true
    };
    // this.getData()

  }
  
      
  
  // getData()
  // {
  //   this._game.getData();
  // }


  hideAndResetScreen()
  {
  this.setState({ show: false });
  window.location.reload();
  }

  hide = () => this.hideAndResetScreen();

    render() {
        return (
          <div>
          <Modal
          show={this.state.show}
          onHide={this.hide}
          size="lg"
          aria-labelledby="contained-modal-title-vcenter"
          centered
          backdrop="static"
        >
          <Modal.Header>
            <Modal.Title id="contained-modal-title-vcenter">
              Result
            </Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <p>
              Total Number Of Valid Words Entered: {this.refs.abc}
              {/* <Game ref={(ref) => this._game = ref}/> */}
            </p>
            <br/>
            <p>
              Total Score:
            </p>
          </Modal.Body>
          <Modal.Footer>
            <Button onClick={this.hide}>Close and Refresh</Button>
          </Modal.Footer>
        </Modal>
        
        </div>
        );
      }
}
