import React, { Component } from 'react';
import {
  shuffledGrid,
  // copyBoard,
  // isTileEqual,
  // isAdjacent,
  calculateScore
} from '../../util/gameUtil';
import Board from '../Board';
import ScoreBox from '../ScoreBox';
// import CurrentWord from '../CurrentWord';
import Button from '../Button';
import './Game.css';

//Again like in App.js since {component} was imported then in the below line it is written as extends Component
//otherwise it should be written as extends React.Component
export default class Game extends Component {
  constructor(props) {
    super(props);
    // TODO: Init board with random tiles
    this.initGrid = shuffledGrid();
    // TODO: Init state with the board
    this.state = {
      board: this.initGrid,
      currentWord: '',
      currentWordPosition: [],
      wordScoreList: {},
      currentLetter:''
    };
  }

  handleChange(value)
  {
    this.setState({currentWord:value});
  }

  handleKeyPress(key)
  {
    this.setState({currentLetter: key});
    // let keyPressed = value;
  }
  // Adds Current Word to the Word List
  handleSubmit(word) {
    // TODO: Check if Current Word is valid

    // Check if word is valid
    if (word.length < 3 || this.state.wordScoreList[word]) {
      return;
    }
    // TODO: Calculate score
    const score = calculateScore(word);

    // TODO: Unselect all tiles.
    const clearedBoard = this.initGrid;

    // TODO: Add to the Word List
    this.setState({
      // wordScoreList: Object.assign(this.state.wordScoreList, {[word]: score}),
      wordScoreList: { ...this.state.wordScoreList, [word]: score },
      currentWord: '',
      currentWordPosition: [],
      board: clearedBoard
    });

  }


  render() {
    return (
      <div>
        <div className="game-area">
          <Board
            board={this.state.board}
          />

          {/* <CurrentWord currentWord={this.state.currentWord}  label="Current Word"> */}
          {/* </CurrentWord> */}

          <div className="word-area">
            <div className="current-header">
              Current Word
            </div>
           <div className="current-word">

            {/* {this.state.currentWord} */}
            <input type="text" onKeyDown = {this.handleKeyPress} onChange={(e) =>this.handleChange(e.target.value)} />
            </div>
          </div>



          <Button
            // TODO: Pass Button Props and Button Callback
            handleSubmit={this.handleSubmit.bind(this, this.state.currentWord)}
            label="SUBMIT WORD"
          />
        </div>

        <ScoreBox
          // TODO: Pass ScoreBox Props
          wordScoreList={this.state.wordScoreList}
          totalScore={Object.values(
            this.state.wordScoreList
          ).reduce((totalScore, next) => {
            return totalScore + next;
          }, 0)}
        />
        {/* Makes Board and ScoreBox be side by side */}
        <div className="clear" />

      </div>
    );
  }
}
