import React,{Component} from 'react';  
import Result from '../Result';
  export default class ClockInterface extends Component {
    constructor(props) {
      super(props);
      this.state = {
        count: 60 * 0.1,
        myInterval: undefined,
        condition:false
      }
    //   this.handleLoad  = this.handleLoad(this);
    
    };
    

    componentDidMount() {
        window.addEventListener('load', this.handleLoad(this));
    }

    displayModalPopUp ()
    {
        return (
            <div className="row">
                <div className="col-md-1">
                    {lineGroup.Pay}
                </div>
                <div className="col-md-3">
                    <strong>{this.getLineInfo(lineGroup.HomeInfo)}</strong>
                </div>
                <div className="col-md-3">
                    <strong>{this.getLineInfo(lineGroup.Score)}</strong>
                </div>
                <div className="col-md-3">
                    <strong>{this.getLineInfo(lineGroup.AwayInfo)}</strong>
                </div>
            </div>
        )
    }

    componentDidUpdate(){
        if(this.state.count <= 0){
            clearInterval(this.state.myInterval);
            this.displayModalPopUp();
            console.log("Hello there");
        }
    }

    getSeconds() {
      return ('0' + this.state.count % 60).slice(-2)
    };
    getMinutes() {
      return (Math.floor(this.state.count / 60) )
    };
  
    handleLoad () {
      var _this = this;
      if (this.state.myInterval) 
      {
        return
      }
      const myInterval = setInterval(function() {
        _this.setState(state => ({
          count: state.count - 1
        }))
      }, 1000)
      this.setState({myInterval})
    };
    
    render() {
      return (
        
        <div id='container'>
          <h1 id='clockdigits'>{this.getMinutes()}:{this.getSeconds()}</h1>
          <div >
            {this.state.condition?<Result/>:null}
        </div> 
        </div>
       
      );
    }
  }
